DECLARE
    LV_DATE   DATE := SYSDATE;
    LV_USER   VARCHAR2 (60) := USER;
    LV_PIDM   NUMBER (10) := NULL;
BEGIN
    BEGIN
        SELECT SPRIDEN_PIDM
          INTO LV_PIDM
          FROM SPRIDEN
         WHERE SPRIDEN_ID = LV_USER AND SPRIDEN_CHANGE_IND IS NULL;
    EXCEPTION
        WHEN NO_DATA_FOUND
        THEN
            LV_PIDM := NULL;
        WHEN TOO_MANY_ROWS
        THEN
            LV_PIDM := NULL;
    END;

    UPDATE BANINST1.ZSRLOCA
       SET ZSRLOCA_ANUM = LV_USER,
           ZSRLOCA_CREATED_BY = :CREATED_BY,
           ZSRLOCA_CREATED_DATE = :CREATED_DATE,
           ZSRLOCA_TERM_CODE = :TERM_CODE,
           ZSRLOCA_UPDATED_BY = LV_USER,
           ZSRLOCA_UPDATED_DATE = LV_DATE,
           ZSRLOCA_ZIP = :ZIP_CODE
     WHERE ZSRLOCA_SURROGATE_ID = :id;
END;

